<template>
  <div>
    <div id="ggb-element"></div>
    <button @click="getBase64String">导出</button>
    <button @click="importBase64">导入</button>
  </div>
</template>

<script setup>
import { onMounted, ref } from 'vue';
// import '../src/utils/deployggb.js';


let ggbApplet = null;
const base64String = ref('');
const ggbApi = ref(null);
onMounted(async () => {
  // 动态加载 GeoGebra 脚本
  await loadGeoGebraScript();

  ggbApplet = new GGBApplet({
    appName: 'classic',
    width: 1900,
    height: 600,
    showToolBar: true,
    showAlgebraInput: true,
    showMenuBar: false,
    language: "zh_CN",
    appletOnLoad: function (api) {
      console.log("GeoGebra applet loaded successfully!");
      // 使用 api 对象进行操作
      ggbApi.value = api;
    }
  });

  ggbApplet.inject('ggb-element');
});

// 动态加载脚本的函数
function loadGeoGebraScript() {
  return new Promise((resolve, reject) => {
    const script = document.createElement('script');
    script.src = "../src/utils/deployggb.js";
    script.onload = resolve;
    script.onerror = reject;
    document.head.appendChild(script);
  });
}


function getBase64String() {
  if (ggbApi.value) {
    ggbApi.value.getBase64(function (base64) {
      console.log("Base64 string:", base64);
      // 这里可以处理 base64 字符串，例如显示、保存或发送到服务器
    });
  } else {
    console.error("GeoGebra API 尚未加载完成。");
  }
}

function importBase64() {
  // if (ggbApi.value && base64String.value) {
  base64String.value = `
  
  `;
  ggbApi.value.setBase64(base64String.value, function () {
    console.log("Base64 字符串已成功导入到 GeoGebra 应用中。");
  });
  // } else {
  //   console.error("GeoGebra API 尚未加载完成或 Base64 字符串为空。");
  // }
}

</script>

